      //  var inputPattern = /^[\u0900-\u097F\u0B00-\u0B7Fa-zA-Z0-9\s@.,_-]*$/;
      var inputPattern = /^[\u0900-\u097F\u0B00-\u0B7Fa-zA-Z0-9\s@.,_:-]*$/;

        var inputValue = $(this).val();
        if (!inputPattern.test(inputValue)) {
            return $(this).val(inputValue.replace(/[^\u0900-\u097F\u0B00-\u0B7Fa-zA-Z0-9@&#_,.\s/-]/g, ''));
        }

        // to prevent input starting with negative sign
        if (inputValue.startsWith('-')) {
            return $(this).val(inputValue.replace(/^-/, ''));
        }

        // Limit text input to 256 characters
        let inputTypes = ['text','search','email'];
        let inputType = $(this).attr('type');
        if( inputType == 'text' || inputType == 'search' || inputType == 'email') {
            if(inputValue.length > 256) {
                  return $(this).val(inputValue.substring(0, 256));
            }
        }

