      {
        if (type == 1) {
          $('#readMore'+counter).hide();
          $('#readLess'+counter).show();
          $('#SecSec'+counter).show();
        } else {
          $('#readMore'+counter).show();
          $('#readLess'+counter).hide();
          $('#SecSec'+counter).hide();
        }
        $('#messages').stop().animate({
          scrollTop: $('#messages')[0].scrollHeight
        }, 800);
      }
      AddReadMore(0,1);
